import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ItemList() {
  const [items, setItems] = useState([]);
  const [searchName, setSearchName] = useState('');

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = async () => {
    try {
      const response = await axios.get('/items');
      setItems(response.data);
    } catch (error) {
      console.error('Error fetching items:', error);
    }
  };

  const handleBorrow = async (id) => {
    try {
      await axios.post(`/items/${id}/borrow`);
      fetchItems(); // ดึงข้อมูลใหม่หลังยืม
    } catch (error) {
      alert(error.response?.data?.message || 'เกิดข้อผิดพลาดในการยืม');
    }
  };

  const filteredItems = items.filter((item) =>
    item.name.toLowerCase().includes(searchName.toLowerCase())
  );

  return (
    <div style={{ padding: '20px' }}>
      <input
        type="text"
        placeholder="ค้นหาชื่อ..."
        value={searchName}
        onChange={(e) => setSearchName(e.target.value)}
        style={{ width: '300px', marginRight: '10px' }}
      />
      <button onClick={fetchItems} style={{ padding: '5px 10px' }}>
        ค้นหา
      </button>

      <table style={{ width: '100%', marginTop: '20px', borderCollapse: 'collapse' }}>
        <thead>
          <tr>
            <th>ชื่อ</th>
            <th>หมวดหมู่</th>
            <th>จำนวนคงเหลือ</th>
            <th>การยืม</th>
          </tr>
        </thead>
        <tbody>
          {filteredItems.map((item) => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.category}</td>
              <td>{item.available}</td>
              <td>
                <button
                  onClick={() => handleBorrow(item.id)}
                  disabled={item.available === 0}
                  style={{ backgroundColor: '#007bff', color: 'white', padding: '5px 10px' }}
                >
                  ยืม
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ItemList;
