import React, { useEffect, useState } from 'react';
import axios from 'axios';

function ItemList() {
  const [items, setItems] = useState([]);
  const [nameSearch, setNameSearch] = useState('');

  useEffect(() => {
    fetchItems();
  }, []);

  const fetchItems = () => {
    axios.get(`/api/items?name=${nameSearch}`)
      .then(response => setItems(response.data))
      .catch(error => console.error('Error fetching items:', error));
  };

  const borrowItem = (itemId) => {
    axios.post(`/api/borrow`, null, { params: { itemId } })
      .then(response => {
        alert(response.data);
        fetchItems(); // อัปเดตจำนวนคงเหลือ
      })
      .catch(error => {
        console.error(error);
        alert(error.response?.data || "เกิดข้อผิดพลาด");
      });
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4">รายการสิ่งของที่สามารถยืมได้</h2>

      <div className="d-flex mb-3">
        <input
          type="text"
          className="form-control me-2"
          placeholder="ค้นหาชื่อ..."
          value={nameSearch}
          onChange={(e) => setNameSearch(e.target.value)}
          style={{ width: '400px' }}
        />
        <button className="btn btn-primary" onClick={fetchItems}>ค้นหา</button>
      </div>

      <table className="table table-bordered text-center">
        <thead className="table-dark">
          <tr>
            <th>ชื่อ</th>
            <th>หมวดหมู่</th>
            <th>จำนวนคงเหลือ</th>
            <th>การยืม</th>
          </tr>
        </thead>
        <tbody>
          {items.map(item => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.category}</td>
              <td>{item.quantity}</td>
              <td>
                <button
                  className="btn btn-sm btn-primary"
                  onClick={() => borrowItem(item.id)}
                  disabled={item.quantity <= 0}
                >
                  ยืม
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ItemList;
