import React, { useEffect, useState } from "react";
import axios from "axios";
import "./App.css";

function ItemList() {
  const [items, setItems] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  const fetchItems = async () => {
    try {
      const response = await axios.get("/api/items");
      setItems(response.data);
    } catch (error) {
      console.error("Error fetching items:", error);
    }
  };

  const handleBorrow = async (itemId) => {
    try {
      const response = await fetch(`http://localhost:8080/api/borrow?itemId=${itemId}`, {
        method: 'POST'
      });

      const result = await response.text(); // ✅ ดึงข้อความออกมา

      alert(result); // แสดงข้อความ "ยืมสำเร็จ" หรือ "ของหมดแล้ว"
      fetchItems();  // โหลดรายการใหม่
    } catch (error) {
      console.error("เกิดข้อผิดพลาด:", error);
      alert("ยืมไม่สำเร็จ");
    }
  };


  useEffect(() => {
    fetchItems();
  }, []);

  const filteredItems = items.filter((item) =>
    item.name.toLowerCase().includes(searchTerm.toLowerCase()) // ✅ ไม่สนพิมพ์เล็กใหญ่
  );

  return (
    <div className="container">
      <h1>ระบบยืมของ</h1>
      <div className="search-container">
        <input
          type="text"
          placeholder="ค้นหาชื่อ..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
      </div>
      <table className="item-table">
        <thead>
          <tr>
            <th>ชื่อ</th>
            <th>หมวดหมู่</th>
            <th>จำนวนคงเหลือ</th>
            <th>การยืม</th>
          </tr>
        </thead>
        <tbody>
          {filteredItems.map((item) => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.category}</td>
              <td>{item.quantity}</td>
              <td>
                <button
                  onClick={() => handleBorrow(item.id)}
                  disabled={item.quantity === 0}
                  className="borrow-button"
                >
                  ยืม
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ItemList;
