import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ItemList() {
  const [items, setItems] = useState([]);
  const [nameSearch, setNameSearch] = useState('');

  const fetchItems = async () => {
    try {
      const response = await axios.get('/api/items', {
        params: { name: nameSearch }
      });
      setItems(response.data);
    } catch (error) {
      console.error('Error fetching items:', error);
    }
  };

  useEffect(() => {
    fetchItems();
  }, []);

  const handleBorrow = async (itemId) => {
    try {
      const response = await axios.post('/api/borrow', null, {
        params: {
          itemId,
          user: 'anonymous' // ไม่จำเป็นต้องใช้จริง แต่ backend ยังรับ param นี้อยู่
        }
      });
      alert(response.data);  // แก้จาก alert(response) เพื่อไม่แสดง [object Object]
      fetchItems(); // รีโหลดข้อมูลใหม่
    } catch (error) {
      console.error(error);
      alert(error.response?.data || 'เกิดข้อผิดพลาด');
    }
  };

  return (
    <div className="container mt-5">
      <h2 className="mb-4 text-center">รายการของที่สามารถยืมได้</h2>

      <div className="d-flex mb-3">
        <input
          type="text"
          className="form-control me-2"
          placeholder="ค้นหาชื่อ..."
          value={nameSearch}
          onChange={(e) => setNameSearch(e.target.value)}
        />
        <button className="btn btn-primary" onClick={fetchItems}>ค้นหา</button>
      </div>

      <table className="table table-bordered text-center">
        <thead className="table-dark">
          <tr>
            <th>ชื่อ</th>
            <th>หมวดหมู่</th>
            <th>จำนวนคงเหลือ</th>
            <th>การยืม</th>
          </tr>
        </thead>
        <tbody>
          {items.map(item => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.category}</td>
              <td>{item.quantity}</td>
              <td>
                <button
                  className="btn btn-sm btn-primary"
                  onClick={() => handleBorrow(item.id)}
                  disabled={item.quantity <= 0}
                >
                  ยืม
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ItemList;
