import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

function ItemList() {
  const [items, setItems] = useState([]);
  const [keyword, setKeyword] = useState('');
  const [category, setCategory] = useState('');

  const fetchItems = async () => {
    try {
      const res = await axios.get(`/api/items?name=${keyword}&category=${category}`);
      setItems(res.data);
    } catch (err) {
      alert("โหลดรายการไม่สำเร็จ");
    }
  };

  useEffect(() => {
    fetchItems();
  }, []);

  const handleBorrow = async (id) => {
    try {
      await axios.post(`/api/borrow?itemId=${id}`);
      fetchItems(); // refresh after borrowing
    } catch (err) {
      alert(err.response?.data || "เกิดข้อผิดพลาดในการยืม");
    }
  };

  return (
    <div className="container">
      <div className="search-bar">
        <input
          placeholder="ค้นหาชื่อ..."
          value={keyword}
          onChange={(e) => setKeyword(e.target.value)}
        />
        <input
          placeholder="หมวดหมู่..."
          value={category}
          onChange={(e) => setCategory(e.target.value)}
        />
        <button onClick={fetchItems}>ค้นหา</button>
      </div>

      <table>
        <thead>
          <tr>
            <th>ชื่อ</th>
            <th>หมวดหมู่</th>
            <th>จำนวนคงเหลือ</th>
            <th>การยืม</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item) => (
            <tr key={item.id}>
              <td>{item.name}</td>
              <td>{item.category}</td>
              <td>{item.quantity}</td>
              <td>
                <button
                  onClick={() => handleBorrow(item.id)}
                  disabled={item.quantity <= 0}
                >
                  {item.quantity > 0 ? 'ยืม' : 'หมด'}
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ItemList;
